import styled from "styled-components"
import { TextField, Button} from '@material-ui/core/';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import { useState } from "react";
import axios from "axios"

const Login = () => {
    const [user, setUser] = useState({username: "", password: ""})
console.log(user)

const handleUsername = (text) => {
    setUser({...user, username: text})
}

const handlePassword = (text) => {
    setUser({...user, password: text})
}

const handleSubmit = (e) => {
    e.preventDefault()
    axios.post("ortex.com/login", user)
    .then(response =>{
        console.log(response)
    })
    .catch(error => {
        console.log(error)
    })
    alert("adsf")
}


    return  <ThemeProvider theme={theme}>
        <PageWrapper>
            <LoginWrapper>
                <Title>Access your account</Title>
                <Form onSubmit={handleSubmit} >
                    <Input 
                    onChange={(e) => handleUsername(e.target.value)} 
                    InputLabelProps={style.labelStyle} 
                    label="Username" 
                    InputProps={style.textStyle} 
                    >
                    </Input>
                    <Input
                     type="password" 
                     onChange={(e) => handlePassword(e.target.value)} 
                     InputLabelProps={style.labelStyle} 
                     InputProps={style.textStyle} 
                     label="Password"></Input>
                    <LoginButton type="submit" value="Submit"  variant="outlined" color="primary">Log in</LoginButton>
                </Form>
            </LoginWrapper>
        </PageWrapper>
        </ThemeProvider>
}

export default Login;



const style = { labelStyle : {style: { color: '#fff' }},
    textStyle : {style : {color: "white"}}
}



const theme = createMuiTheme({

        palette: {
          primary: {
              main: "#99e4ee"},
        action : {
            disabledBackground: "#337f83",
            disabled: "#337f83"
        }
        }
      })


const Input = styled(TextField)`
    color: white;
      `


const LoginButton = styled(Button)`
    padding-top: 1em;
`

const Form = styled.form`

    display: flex;
    flex-direction: column;
    justify-content: space-around ;
    width: 70%;
    height: 70%;
`



const Title = styled.h1`   
    margin-top: 2.5em;
    font-weight: 100;
    letter-spacing: 0.1em;
`

const PageWrapper = styled.div`
    height: 100vh;
    margin: 0;
    background-color: #1f222e;
    display: flex;
    justify-content: center;
`

const LoginWrapper = styled.div`
    padding: 1em;
    display: flex;
    flex-direction: column;
    align-items: center;
    border-radius: 5px;
    margin-top: 4em;
    border: 1px solid white;
    width: 40em;
    height: 40em;
    background: -webkit-linear-gradient(45deg,  #35394a -70%,#1f222e 100%); 
`
