import {
  Dialog as DialogUI,
  DialogTitle,
  DialogContent,
  Input as InputUI,
  Button,
  TextField
} from "@material-ui/core";
import styled from "styled-components";

const Dialog = ({ open, onClose }) => {
  const handleOnSubmit = (e) => {
    e.preventDefault();
    console.log("error");
  };
  return (
    <StyledDialog fullWidth open={open} onClose={onClose}>
      <DialogTitle>
        <Title>Change your password</Title>
      </DialogTitle>
      <DialogContent>
        <form onSubmit={handleOnSubmit}>
        <TextField
              type="password"
              InputLabelProps={style.labelStyle}
              InputProps={style.textStyle}
              label="Password"
            ></TextField>
                      <Input placeholder="Old password" type="password"></Input>
          <Input type="password" placeholder="New password"></Input>
          <Button type="submit">Submit change</Button>
        </form>
      </DialogContent>
    </StyledDialog>
  );
};

export default Dialog;

const style = {
  labelStyle: { style: { color: "#fff" } },
  textStyle: { style: { color: "white" } },
};


const StyledDialog = styled(DialogUI)`
  .MuiDialog-paper {
    background: -webkit-linear-gradient(45deg, #35394a -70%, #1f222e 100%);
    color: white;
  }
`;

const Title = styled.h2`
  font-weight: 300;
  letter-spacing: 0.2em;
  display: flex;
  justify-content: center;
  margin-top: 0;
`;

const Input = styled(InputUI)`
  width: 100%;
  margin-bottom: 2rem;
`;
