import React, { useState } from "react"
import { Tabs, Tab } from "react-bootstrap"
import { Excel } from "../../components/Excel/Excel"

export const Inicio = () => {
    const [tabs, setTabs] = useState([])
    const [num, setNum] = useState(1)
    const newTab = () => {
        setNum(num + 1)
        setTabs([...tabs, { name: `Sin titulo ${num}`, key: `${num}` }])
    }

    const tittleChange = (titulo, key) => {
        const newTabs = [...tabs]
        const foundIndex = tabs.findIndex(item => item.key === key)
        const obj = tabs[foundIndex]
        obj.name = titulo
        newTabs[foundIndex] = obj
        setTabs(newTabs)
    }
    console.log(tabs)
    return <div>
        <button className="btn btn-outline-success m-2" onClick={newTab}>Nueva Hoja</button>
        <Tabs
            defaultActiveKey="1"
            transition={false}
            className="mb-3 m-2 "
        >
            {tabs.map(item =>
                <Tab key={item.key} title={item.name} eventKey={item.key}><Excel tittleChange={()=>tittleChange()}></Excel></Tab>)}

        </Tabs>
    </div>
}
