import React, { useEffect, useRef, useState } from "react"
import db from "../../firebase/firebase"

export const Excel = (props) => {
    const [profesionales, setProfesionales] = useState([])
    const [titulo, setTitulo] = useState("")
    const [ultimoTitulo, setUltimoTitulo] = useState("")
    useEffect(() => {
        db.collection("profesionales").onSnapshot(querySnapshot => {
            const data = []
            querySnapshot.docs.forEach(doc => {
                const { name } = doc.data()
                data.push({
                    id: doc.id,
                    name
                })
            })
            setProfesionales(data)
        })
    }, [])
    function usePrevious(data){
        const ref = useRef();
        useEffect(()=>{
          ref.current = data
        }, [data])
        return ref.current
      }
      const prevState = usePrevious(titulo)

    useEffect(() => {
        setTitulo(props.titulo)
    }, [props.titulo])
    
    
    console.log(prevState)

      console.log(ultimoTitulo)
    const saveData = async()=> {
         profesionales.map(item=> {
             const {monto, iibb, circ, circalq, bco, subtotal, total} = item
            db.collection("profesionales").doc(item.id).update(
                {
                [titulo] : {
                    monto: monto || 0,
                    iibb: iibb || 0,
                    circ: circ || 0,
                    circalq: circalq || 0,
                    bco: bco || 0,
                    subtotal: subtotal || 0,
                    total: total || 0,
                }
            }, {merge: true})
        })
        if(prevState /= titulo){
            profesionales.map(item=> {
                db.collection("profesionales").doc(item.id).update({[prevState] : db.FieldValue.delete()})
            })
        }
    }


    const conseguirPorcentaje = (num, porcentaje) => {
        return Math.round((porcentaje * num) / 100)
    }
    const circalq = (value, id) => {
        const prof = [...profesionales]
        const foundIndex = profesionales.findIndex(item => item.id === id)
        const obj = profesionales[foundIndex]
        obj.circalq = value
        obj.subtotal = obj.monto - obj.iibb - obj.circ - (obj.circalq || 0)
        obj.bco = (conseguirPorcentaje((+obj.subtotal + +obj.monto), 1.2)) + 40
        obj.total = obj.subtotal - obj.bco
        prof[foundIndex] = obj
        setProfesionales(prof)
    }
    const guardarMontos = (value, id) => {
        const prof = [...profesionales]
        const foundIndex = profesionales.findIndex(item => item.id === id)
        const obj = profesionales[foundIndex]
        obj.monto = +value
        obj.iibb = conseguirPorcentaje(value, 3.5)
        obj.circ = conseguirPorcentaje(value, 5)
        obj.subtotal = obj.monto - obj.iibb - obj.circ - (obj.circalq || 0)
        obj.bco = (conseguirPorcentaje((+obj.subtotal + +obj.monto), 1.2)) + 40
        obj.total = obj.subtotal - obj.bco
        prof[foundIndex] = obj
        setProfesionales(prof)
    }
    const titulos = ["MONTO", "IIBB", "%CIRC", "CIRC/ALQ", "SUBTOTAL", "BCO", "TOTAL", "", "", "", "", "", ""]
    return <div className="row">
        <div className="">
            <button className="btn btn-success m-2 float-end" onClick={saveData}>GUARDAR DATOS</button>
        </div>
        <div className=" m-0">
            <div className="row m-0 ">
                <div className="row m-0">
                    <div className="col-2 p-3 border">
                    </div>
                    {titulos.map(item => <div className="col p-0 border">
                        {item}
                    </div>)}
                </div>
            </div>
            <div className="row m-0 ">
                {profesionales.map(item =>
                    <div className="row m-0 ">
                        <div key={item.id} className="col-2 border ">{item.name}</div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" onBlur={(e) => guardarMontos(e.target.value, item.id)} />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" defaultValue={item.iibb || ""} />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" defaultValue={item.circ || ""} />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" onBlur={(e) => circalq(e.target.value, item.id)} />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" defaultValue={item.subtotal || ""} />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" defaultValue={item.bco || ""} />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" defaultValue={item.total || ""} />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" />
                        </div>
                        <div className="col p-0 ">
                            <input type="text" className="form-control" />
                        </div>
                    </div>
                )}
            </div>
        </div>
    </div>
}
