import React, { useState } from "react"
import { Tabs, Tab } from "react-bootstrap"
import { Excel } from "../../components/Excel/Excel"

export const Inicio = () => {
    const [tabs, setTabs] = useState([])
    const [num, setNum] = useState(1)
    const [tittle, setTittle] = useState("")
    const newTab = () => {
        setNum(num+1) 
        setTabs([...tabs, {name: `Sin titulo ${num}`, key: `${num}`}])
    }

    const tittleChange = (titulo) => {
        setTittle(titulo)
    }

    return <div>
        <button className="btn" onClick={newTab}>Nueva Hoja</button>
        <Tabs
            defaultActiveKey="1"
            transition={false}
            className="mb-3 m-2 "
        >
            {tabs.map(item => <Tab  key={item.key} title={tittle} eventKey={item.key}><Excel tittleChange={tittleChange}></Excel></Tab>)}
    
        </Tabs>
    </div>
}
