import styled from "styled-components";
import { TextField, Button } from "@material-ui/core/";
import { createMuiTheme, ThemeProvider } from "@material-ui/core/styles";
import { useState } from "react";
import data from "../../data/data"
import ClipLoader from "react-spinners/ClipLoader"

const Login = () => {
  const [user, setUser] = useState({ username: "", password: "" });
  const [isPending, setIsPending] = useState(false)

  const handleUsername = (text) => {
    setUser({ ...user, username: text });
  };

  const handlePassword = (text) => {
    setUser({ ...user, password: text });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsPending(true)
    if (data.some((element) => element.username === user.username  && element.password === user.password)) {
      console.log("success")
    } else{
      console.log("failure")
    }
  };



  return (
    <ThemeProvider theme={theme}>
      <PageWrapper>
        <LoginWrapper>
          <Title>Access your account</Title>
          <Form onSubmit={handleSubmit}>
            <Input
              onChange={(e) => handleUsername(e.target.value)}
              InputLabelProps={style.labelStyle}
              label="Username"
              InputProps={style.textStyle}
            ></Input>
            <Input
              type="password"
              onChange={(e) => handlePassword(e.target.value)}
              InputLabelProps={style.labelStyle}
              InputProps={style.textStyle}
              label="Password"
            ></Input>
            {!isPending? <LoginButton
              type="submit"
              value="Submit"
              variant="outlined"
              color="primary"
            >
              Log in
            </LoginButton> : <LoginButton
              type="submit"
              value="Submit"
              variant="outlined"
              color="primary"
              disabled={true}
            >
              Login in <ClipLoader color={"#337f83"} size={10} />
            </LoginButton>}
          </Form>
        </LoginWrapper>
      </PageWrapper>
    </ThemeProvider>
  );
};

export default Login;


const style = {
  labelStyle: { style: { color: "#fff" } },
  textStyle: { style: { color: "white" } },
};

const theme = createMuiTheme({
  palette: {
    primary: {
      main: "#337f83",
    },
    action: {
      disabledBackground: "#337f83",
      disabled: "#337f83",
    },
  },
});


const Input = styled(TextField)`
  color: white;
`;

const LoginButton = styled(Button)`
  padding-top: 1em;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 70%;
  height: 70%;
`;

const Title = styled.h1`
  margin-top: 2.5em;
  font-weight: 100;
  letter-spacing: 0.1em;
`;

const PageWrapper = styled.div`
  height: 100vh;
  margin: 0;
  background-color: #1f222e;
  display: flex;
  justify-content: center;
`;

const LoginWrapper = styled.div`
  padding: 1em;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 5px;
  margin-top: 4em;
  border: 1px solid white;
  width: 40em;
  height: 40em;
  background: -webkit-linear-gradient(45deg, #35394a -70%, #1f222e 100%);
`;
