import styled from "styled-components"
import { TextField, Button} from '@material-ui/core/';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import { useState } from "react";


const Login = () => {
    const [user, setUser] = useState({username: "", password: ""})
    const [successUsername, setSuccessUsername] = useState(false)
    const [successPassword, setSuccessPassword] = useState(false)
    const [successRepeatPassword, setSuccessRepeatPassword] = useState(false)

console.log(user)

const handleUsername = (text) => {
    if(text && text.length < 3 )
    {
        setSuccessUsername(true)
    } if (text && text.length > 3)
    {
        setSuccessUsername(false)
    }
    setUser({...user, username: text})
}

const handlePassword = (text) => {
    if(text && text.length < 7 )
    {
        setSuccessPassword(true)
    } if (text && text.length > 7)
    {
        setSuccessPassword(false)
    }
    setUser({...user, password: text})
}

const handleSubmit = (e) => {
    e.preventDefault()
    alert("adsf")
}

const handleRepeatPassword = (text) => {
    if(text !== user.password) {
        setSuccessRepeatPassword(true)
    } if (text === user.password) {
        setSuccessRepeatPassword(false)
    }
}


    return  <ThemeProvider theme={theme}>
        <PageWrapper>
            <LoginWrapper>
                <Title>Access your account</Title>
                <Form onSubmit={handleSubmit} >
                    <Input 
                    onChange={(e) => handleUsername(e.target.value)} 
                    InputLabelProps={style.labelStyle} 
                    label="Username" 
                    InputProps={style.textStyle} 
                    error={successUsername ?  true : false }
                    >
                    </Input>
                    {successUsername ?  <ErrorText>Username must have at least 4 characters</ErrorText> : <div></div>}
                    <Input
                     type="password" 
                     onChange={(e) => handlePassword(e.target.value)} 
                     InputLabelProps={style.labelStyle} 
                     InputProps={style.textStyle} 
                     error={successPassword ?  true : false }
                     label="Password"></Input>
                     {successPassword ?  <ErrorText>Password must have at least 8 characters</ErrorText> : <div></div>}
                     <Input 
                     type="password"
                     onChange={(e) => handleRepeatPassword(e.target.value)}
                     InputLabelProps={style.labelStyle}
                     InputProps={style.textStyle}
                     error={successRepeatPassword ? true: false}
                     label="Repeat password"
                     >
                     </Input>
                     {successRepeatPassword ?  <ErrorText>Password don't match</ErrorText> : <div></div>}
                    <LoginButton disabled={true} type="submit" value="Submit"  variant="outlined" color="primary">Log in</LoginButton>
                </Form>
            </LoginWrapper>
        </PageWrapper>
        </ThemeProvider>
}

export default Login;


const ErrorText = styled.h4`
    color: #f44336;
    font-weight: lighter;
`

const style = { labelStyle : {style: { color: '#fff' }},
    textStyle : {style : {color: "white"}}
}



const theme = createMuiTheme({

        palette: {
          primary: {
              main: "#33c9dc"},
        action : {
            disabledBackground: "red",
            disabled: "secondary"
        }
        }
      })


const Input = styled(TextField)`
    color: white;
      `


const LoginButton = styled(Button)`
    padding-top: 1em;
`

const Form = styled.form`

    display: flex;
    flex-direction: column;
    justify-content: space-around ;
    width: 70%;
    height: 70%;
`



const Title = styled.h1`   
    margin-top: 2.5em;
    font-weight: 100;
    letter-spacing: 0.1em;
`

const PageWrapper = styled.div`
    height: 100vh;
    margin: 0;
    background-color: #1f222e;
    display: flex;
    justify-content: center;
`

const LoginWrapper = styled.div`
    padding: 1em;
    display: flex;
    flex-direction: column;
    align-items: center;
    border-radius: 5px;
    margin-top: 4em;
    border: 1px solid white;
    width: 40em;
    height: 40em;
    background: -webkit-linear-gradient(45deg,  #35394a -70%,#1f222e 100%); 
`
