import { useEffect, useState } from "react";
import { connect } from "react-redux";
import showUsers from "../../redux/actions/showUsers";

const ShowUsers = (props) => {
  // const [users, setUsers] = useState([]);
  const { usersShow } = props;
  const users = usersShow

  const getUsers = async () => {
    await props.showUsers()
  }
  useEffect( () => {
    getUsers()
    // setUsers(usersShow);
    console.log(users.length);
    console.log(usersShow);
  }, []);

  const handleButton = () => {
    props.showUsers();
    // setUsers(usersShow);
    console.log(users);
    console.log(usersShow);
  };

  return (
    <div>
      <button onClick={handleButton}>aaa</button>
      {usersShow.length > 0 ? (
        usersShow.map((user) => {
          return <li key={user.id}>{user.name}</li>;
        })
      ) : (
        <div>hola</div>
      )}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    usersShow: state.usersShow,
  };
};

const mapDispatchToProps = {
  showUsers,
};

export default connect(mapStateToProps, mapDispatchToProps)(ShowUsers);
