import React, { useState } from "react";
import { connect } from "react-redux";
import Button from "../../components/Button/Button";
import Dialog from "../../components/Dialog/Dialog";
import createUser from "../../redux/actions/createUser"

const CreateUser = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const [user, setUser] = useState({username: "", password: ""})


  this.onChangePassword = this.onChangeText.bind(this);
  this.onChangeUsername = this.onChangeText.bind(this);
  this.onChangeSelection = this.onChangeSelection.bind(this);
 
  const onChangePassword = (text) => {
    setUser({ ...user, password: text });
  }
  const onChangeUsername = (text) => {
    setUser({ ...user, username: text });
  }
  const onChangeSelection = (user) => {
      createUser(user);
  }
  console.log(user)
  const handleCloseDialog = () => {
    setIsOpen(false);
  };
  const handleButtonClick = () => {
    setIsOpen(true);
  };

  

  return (
    <div>
      <Button onClick={handleButtonClick}>Create User</Button>
      <Dialog
        open={isOpen}
        title="CREATE USER"
        onClose={handleCloseDialog}
        onChangeUsername={onChangeUsername}
        onChangePassword={onChangePassword}
        onChangeSelection={onChangeSelection}  
      ><Button onClick={onChangeSelection}>Create User</Button>
      </Dialog>
    </div>
  );
};


const mapStateToProps = (state) => {
  return {
    userCreation: state.userCreation
  } 
}


const mapDispatchToProps = {
  createUser,
};

export default connect(mapStateToProps, mapDispatchToProps)(CreateUser);;
